import { container } from "assets/jss/material-kit-react.js";

const componentsStyle = {
  container,
  brand: {
    color: "#000000",
    textAlign: "left"
  },
  title: {
    fontSize: "4.2rem",
    fontWeight: "600",
    display: "inline-block",
    position: "relative"
  },
  subtitle: {
    fontSize: "1.313rem",
    maxWidth: "500px",
    margin: "10px 0 0"
  },
  main: {
    background: "#FFFFFF",
    position: "relative",
    zIndex: "3"
  },
   mainRight: {
    background: "#FFFFFF",
    textAlign: "right",
    position: "relative",
    zIndex: "2"
  },
  mainRaised: {
    margin: "-60px 30px 0px",
    borderRadius: "6px",
    boxShadow:
      "0 16px 24px 2px rgba(0, 0, 0, 0.14), 0 6px 30px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.2)"
  },
  link: {
    textDecoration: "none"
  },
  split: {
    height: "100%",
    width: "50%",
    position: "fixed",
    top: "0"
  },

  /* Control the left side */
  left: {
    left: "0",
    background: "#1111111",
  },

/* Control the right side */
right: {
  right: 0,
  background: "#3242",
},

/* If you want the content centered horizontally and vertically */
centered: {
  position: "absolute",
  top: "50%",
  left: "50%",
  // transform: translate(-50%, -50%),
  textAlign: "center",
},

  /* Style the image inside the centered container, if needed */
  centeredimg: {
    width: "150",
    borderRadius: "50%",
  },

  textCenter: {
    textAlign: "center"
  }
};

export default componentsStyle;
